/**
 * @param {number[]} nums
 * @return {boolean}
 */
var containsDuplicate = function(nums) {
    const sorted = nums.sort()
    while(sorted.length > 0) {
        const n = sorted.shift()
        if (sorted[0] === n) return true
    }
    return false
};

/**
 * @param {number[]} nums
 * @return {boolean}
 */
var containsDuplicate = function(nums) {
    let set = new Set()

    for(let num of nums){

        if(set.has(num))  return true
        set.add(num)
    }

    return false
};

